import{_ as e,c as s,o as a,b as n}from"./app.fba10273.js";const m=JSON.parse('{"title":"CSS 9\u6708\u90E8\u5206\u603B\u7ED3","description":"","frontmatter":{},"headers":[{"level":3,"title":"\u5BF9img\u6807\u7B7E\u4F7F\u7528before\u548Cafter\u4F2A\u5143\u7D20\u5931\u6548","slug":"\u5BF9img\u6807\u7B7E\u4F7F\u7528before\u548Cafter\u4F2A\u5143\u7D20\u5931\u6548","link":"#\u5BF9img\u6807\u7B7E\u4F7F\u7528before\u548Cafter\u4F2A\u5143\u7D20\u5931\u6548","children":[]},{"level":3,"title":"\u5BF9\u4E8Esvg\uFF0C\u6216\u8005img\u56FE\u7247\u7684\u5F02\u8272\u5904\u7406  filter : drop-shadwo()","slug":"\u5BF9\u4E8Esvg\uFF0C\u6216\u8005img\u56FE\u7247\u7684\u5F02\u8272\u5904\u7406-filter-drop-shadwo","link":"#\u5BF9\u4E8Esvg\uFF0C\u6216\u8005img\u56FE\u7247\u7684\u5F02\u8272\u5904\u7406-filter-drop-shadwo","children":[]},{"level":3,"title":"img\u610F\u5916\u6491\u5F00div\u7684\u95EE\u9898","slug":"img\u610F\u5916\u6491\u5F00div\u7684\u95EE\u9898","link":"#img\u610F\u5916\u6491\u5F00div\u7684\u95EE\u9898","children":[]},{"level":3,"title":"\u66F4\u6539\u5143\u7D20\u53D8\u5F62\u7684\u539F\u70B9 transform-origin","slug":"\u66F4\u6539\u5143\u7D20\u53D8\u5F62\u7684\u539F\u70B9-transform-origin","link":"#\u66F4\u6539\u5143\u7D20\u53D8\u5F62\u7684\u539F\u70B9-transform-origin","children":[]},{"level":3,"title":"\u5728el-popper\u4E0A\u6539\u6837\u5F0F","slug":"\u5728el-popper\u4E0A\u6539\u6837\u5F0F","link":"#\u5728el-popper\u4E0A\u6539\u6837\u5F0F","children":[]}],"relativePath":"articles/myCss/CSS202209.md"}'),r={name:"articles/myCss/CSS202209.md"},t=n(`<h1 id="css-9\u6708\u90E8\u5206\u603B\u7ED3" tabindex="-1">CSS 9\u6708\u90E8\u5206\u603B\u7ED3 <a class="header-anchor" href="#css-9\u6708\u90E8\u5206\u603B\u7ED3" aria-hidden="true">#</a></h1><h3 id="\u5BF9img\u6807\u7B7E\u4F7F\u7528before\u548Cafter\u4F2A\u5143\u7D20\u5931\u6548" tabindex="-1">\u5BF9img\u6807\u7B7E\u4F7F\u7528before\u548Cafter\u4F2A\u5143\u7D20\u5931\u6548 <a class="header-anchor" href="#\u5BF9img\u6807\u7B7E\u4F7F\u7528before\u548Cafter\u4F2A\u5143\u7D20\u5931\u6548" aria-hidden="true">#</a></h3><p>\u200B \u5728W3C\u4E2D\u5BF9\u4F2A\u5143\u7D20 before \u548C after \u4E2D\u7684\u5B9A\u4E49\uFF1A</p><pre><code>As their names indicate, the :before and :after pseudo-elements specify the location of content before and after an element&#39;s document tree content.
</code></pre><p><a href="https://www.w3.org/TR/CSS21/generate.html#before-after-content" target="_blank" rel="noreferrer">https://www.w3.org/TR/CSS21/generate.html#before-after-content</a></p><p><img>\u662F\u4E00\u4E2A<strong>\u66FF\u6362\u5143\u7D20</strong>\uFF0C\u5176\u5916\u89C2\u548C\u5C3A\u5BF8\u662F\u7531\u5916\u90E8\u8D44\u6E90\u6765\u51B3\u5B9A\u7684\uFF0C\u5F53\u5916\u90E8\u56FE\u7247\u8D44\u6E90\u52A0\u8F7D\u5931\u8D25\u65F6\u5176\u4F1A\u663E\u793A\u7834\u88C2\u56FE\u7247\u548Calt\u6587\u5B57\uFF0C\u5C3A\u5BF8\u4EC5\u7531\u5176\u81EA\u8EAB\u5185\u5BB9\u51B3\u5B9A\u3002<strong>\u8FD9\u65F6<img>\u5143\u7D20\u53EF\u4EE5\u4F7F\u7528\u4F2A\u5143\u7D20:before\u548C:after\uFF0C\u56E0\u4E3A\u5176\u5143\u7D20\u5185\u5BB9\u6CA1\u6709\u88AB\u66FF\u6362\uFF01</strong></p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">Note. This specification does not fully define the interaction of :before and :after with replaced elements (such as IMG in HTML). This will be defined in more detail in a future specification.</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u7EFC\u4E0A\uFF0C\u5BF9\u4E8E\u53EF\u66FF\u6362\u5143\u7D20\uFF08\u5982 img\u3001input\u3001select \u7B49\uFF09\u5C3D\u91CF\u907F\u514D\u5728\u5176\u4E0A\u4F7F\u7528\u4F2A\u5143\u7D20</p><h3 id="\u5BF9\u4E8Esvg\uFF0C\u6216\u8005img\u56FE\u7247\u7684\u5F02\u8272\u5904\u7406-filter-drop-shadwo" tabindex="-1">\u5BF9\u4E8Esvg\uFF0C\u6216\u8005img\u56FE\u7247\u7684\u5F02\u8272\u5904\u7406 filter : drop-shadwo() <a class="header-anchor" href="#\u5BF9\u4E8Esvg\uFF0C\u6216\u8005img\u56FE\u7247\u7684\u5F02\u8272\u5904\u7406-filter-drop-shadwo" aria-hidden="true">#</a></h3><div class="language-css"><button class="copy"></button><span class="lang">css</span><pre><code><span class="line"><span style="color:#FFCB6B;">filter</span><span style="color:#A6ACCD;"> : drop-shadow(</span><span style="color:#FFCB6B;">offset-x</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">offset-y</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">blur-radius</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">spread-radius</span><span style="color:#A6ACCD;"> color)</span></span>
<span class="line"></span></code></pre></div><p>MDN\u4E0A\u5BF9\u5176\u63CF\u8FF0\uFF1A</p><p>\u200B \u8FD9\u4E2A\u51FD\u6570\u6709\u70B9\u7C7B\u4F3C\u4E8E box-shadow \u5C5E\u6027\u3002box-shadow \u5C5E\u6027\u5728\u5143\u7D20\u7684\u6574\u4E2A\u6846\u540E\u9762\u521B\u5EFA\u4E00\u4E2A\u77E9\u5F62\u9634\u5F71\uFF0C\u800C drop-shadow() \u8FC7\u6EE4\u5668\u5219\u662F\u521B\u5EFA\u4E00\u4E2A\u7B26\u5408\u56FE\u50CF\u672C\u8EAB\u5F62\u72B6 (alpha \u901A\u9053) \u7684\u9634\u5F71\u3002</p><h3 id="img\u610F\u5916\u6491\u5F00div\u7684\u95EE\u9898" tabindex="-1">img\u610F\u5916\u6491\u5F00div\u7684\u95EE\u9898 <a class="header-anchor" href="#img\u610F\u5916\u6491\u5F00div\u7684\u95EE\u9898" aria-hidden="true">#</a></h3><p>\u200B \u5728\u72EC\u7ACB\u7684img\u5143\u7D20\u5957\u7528div\u4F5C\u4E3A\u7236\u7EA7\u5143\u7D20\u65F6\uFF0C\u5E38\u89C4\u601D\u7EF4\u8BA4\u4E3Adiv\u8FB9\u6846=img\u8FB9\u6846\uFF0C\u4F46\u662F\u5374\u610F\u5916\u4F1A\u53D1\u73B0div\u8FB9\u6846\u591A\u4E864px\u5DE6\u53F3\u7684\u50CF\u7D20\u3002</p><p>\u200B \u539F\u56E0\uFF1Aimg\u5C5E\u4E8E\u884C\u5185\u5143\u7D20\uFF0C\u5728\u7ED3\u675F\u7684\u65F6\u5019\uFF0C\u4F1A\u5728\u672B\u5C3E\u52A0\u4E0A\u4E00\u4E2A\u7A7A\u767D\u7B26\uFF0C\u800C\u7A7A\u767D\u7B26\u9ED8\u8BA4\u4F1A\u6709\u884C\u9AD8\u7684\uFF0C\u6240\u4EE5\u4F1A\u81EA\u52A8\u5C06div\u6491\u5F004px\u3002</p><p>\u200B \u89E3\u51B3\u529E\u6CD5\uFF1Adisplay : inline-block\uFF0Cps\uFF1A<strong>img\u5C5E\u4E8E\u884C\u5185\u5143\u7D20\uFF01</strong></p><h3 id="\u66F4\u6539\u5143\u7D20\u53D8\u5F62\u7684\u539F\u70B9-transform-origin" tabindex="-1">\u66F4\u6539\u5143\u7D20\u53D8\u5F62\u7684\u539F\u70B9 transform-origin <a class="header-anchor" href="#\u66F4\u6539\u5143\u7D20\u53D8\u5F62\u7684\u539F\u70B9-transform-origin" aria-hidden="true">#</a></h3><p>\u4E00\u4E2A\u503C\uFF1A</p><p>\u200B \u5FC5\u987B\u662F <strong>\u957F\u5EA6</strong>\uFF0C<strong>\u767E\u5206\u6BD4</strong>\uFF0C\u6216 **left, center, right, top, bottom **\u5173\u952E\u5B57\u4E2D\u7684\u4E00\u4E2A\u3002</p><p>\u4E24\u4E2A\u503C\uFF1A</p><p>\u200B \u5176\u4E2D\u4E00\u4E2A\u5FC5\u987B\u662F <strong>\u957F\u5EA6</strong>\uFF0C<strong>\u767E\u5206\u6BD4</strong>\uFF0C\u6216 <strong>left, center, right</strong> \u5173\u952E\u5B57\u4E2D\u7684\u4E00\u4E2A\u3002 \u200B \u53E6\u4E00\u4E2A\u5FC5\u987B\u662F <strong>\u957F\u5EA6</strong>\uFF0C<strong>\u767E\u5206\u6BD4</strong>\uFF0C\u6216 <strong>top, center, bottom</strong> \u5173\u952E\u5B57\u4E2D\u7684\u4E00\u4E2A\u3002</p><p>\u4E09\u4E2A\u503C\uFF1A</p><p>\u200B \u524D\u4E24\u4E2A\u503C\u548C\u53EA\u6709\u4E24\u4E2A\u503C\u65F6\u7684\u7528\u6CD5\u76F8\u540C\u3002 \u200B \u7B2C\u4E09\u4E2A\u503C\u5FC5\u987B\u662F <strong>\u957F\u5EA6</strong>\u3002\u5B83\u59CB\u7EC8\u4EE3\u8868 Z \u8F74\u504F\u79FB\u91CF\u3002</p><div class="language-css"><button class="copy"></button><span class="lang">css</span><pre><code><span class="line"><span style="color:#FFCB6B;">transform-origin</span><span style="color:#A6ACCD;">: center; // \u4E2D\u5FC3\u70B9</span></span>
<span class="line"><span style="color:#FFCB6B;">transform-origin</span><span style="color:#A6ACCD;">: top left; // \u5DE6\u4E0A\u89D2</span></span>
<span class="line"><span style="color:#FFCB6B;">transform-origin</span><span style="color:#A6ACCD;">: 50px 50px; // \u5DE6\u4E0A\u89D2\u4E3A\u539F\u70B9 \u5411\u4E0B/\u5411\u53F3\u504F\u79FB50px</span></span>
<span class="line"><span style="color:#FFCB6B;">transform-origin</span><span style="color:#A6ACCD;">: bottom right 60px; //  \u53F3\u4E0B\u89D2\u4E3A\u539F\u70B9\uFF0C 60px\u4EE3\u8868 Z \u8F74\u504F\u79FB\u91CF</span></span>
<span class="line"></span></code></pre></div><p>\u7ED3\u5408 <strong>transform : rotate()</strong> \u5B9E\u73B0\u5143\u7D20\u7684\u65CB\u8F6C\u52A8\u753B</p><h3 id="\u5728el-popper\u4E0A\u6539\u6837\u5F0F" tabindex="-1">\u5728el-popper\u4E0A\u6539\u6837\u5F0F <a class="header-anchor" href="#\u5728el-popper\u4E0A\u6539\u6837\u5F0F" aria-hidden="true">#</a></h3><p>\u200B \u7531\u4E8E\u4E1A\u52A1\u9700\u6C42\uFF0C\u8981\u5728el-popper\u4E0A\u5F39\u51FA\u7684\u5185\u5BB9\u4FEE\u6539\u6837\u5F0F\u3002\u76F4\u63A5\u5728\u5F53\u524Dvue\u6587\u4EF6\u4E2D\u505A\u6837\u5F0F\u7A7F\u900F\u662F\u4E0D\u4F1A\u751F\u6548\u7684\uFF01</p><p>\u9700\u8981\u5728\u4E3B\u5165\u53E3\u6587\u4EF6\u5F15\u5165\u6837\u5F0F\u65F6\u9884\u8BBE\u597D\uFF0C\u5EFA\u8BAE\u65B0\u5EFA\u4E00\u4E2Acustom.css\u6587\u4EF6\uFF0C\u8BBE\u7F6E\u6240\u6709\u7684\u7279\u6B8A\u5F39\u51FA\u6846\u6837\u5F0F\uFF01</p>`,28),o=[t];function p(i,l,c,d,g,f){return a(),s("div",null,o)}const C=e(r,[["render",p]]);export{m as __pageData,C as default};
